import React, { useState, useEffect } from "react";
import "./App.css";
import Login from "./Login";
import { getTokenFromUrl } from "./spotify";
import SpotifyWebApi from "spotify-web-api-js";

const spotify = new SpotifyWebApi();

function App() {
  const [token, setToken] = useState(null);
  //run code based on a given condition i.e[]
  useEffect(() => {
    const hash = getTokenFromUrl();
    //to remove the access token for security kinda reasons
    window.location.hash = "";
    const _token = hash.access_token;

    if (_token) {
      setToken(_token);
      spotify.setAccessToken(_token);
      spotify.getMe().then((user) => {
        console.log("HeyUSHD", user);
      });
    }
    //console.log("I Have A Token ", token);
  }, []);

  return (
    //BEM
    <div className="app">
      {
        // if there is a token start the player or redirect to login page
        token ? <Player></Player> : <Login />
      }
    </div>
  );
}

export default App;
